SET NAMES UTF8;
DROP DATABASE IF EXISTS flower;
CREATE DATABASE flower CHARSET=UTF8;
USE flower;

/**鲜花家族**/
CREATE TABLE flower_family(
  fid INT PRIMARY KEY AUTO_INCREMENT,
  fname VARCHAR(32)
);


/**鲜花**/
CREATE TABLE flower_details(
  lid INT PRIMARY KEY AUTO_INCREMENT,
  family_id INT,              #所属型号家族编号
  title VARCHAR(128),         #主标题
  price DECIMAL(10,2),        #价格
  serial_number VARCHAR(32),  #编号
  material VARCHAR(64),       #材料
  pack VARCHAR(32),           #包装
  blessing VARCHAR(32),       #祝福语
  annexation VARCHAR(32),     #附赠
  distribution VARCHAR(32),   #配送
  state VARCHAR(64),          #说明
  details VARCHAR(1024)       #产品详细说明
);

/**鲜花图片**/
CREATE TABLE flower_pic(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  flower_id INT,                #鲜花编号
  sm VARCHAR(128),              #小图片路径
  md VARCHAR(128),              #中图片路径
  lg VARCHAR(128)               #大图片路径
);	

/**用户信息**/
CREATE TABLE flower_user(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  uname VARCHAR(32),
  upwd VARCHAR(32),
  spwd VARCHAR(64),
  phone VARCHAR(16),
  gender INT                  #性别  0-女  1-男
);


/**收货地址信息**/
CREATE TABLE flower_receiver_address(
  aid INT PRIMARY KEY AUTO_INCREMENT,
  user_id INT,                #用户编号
  receiver VARCHAR(16),       #接收人姓名
  province VARCHAR(16),       #省
  city VARCHAR(16),           #市
  county VARCHAR(16),         #县
  address VARCHAR(128),       #详细地址
  cellphone VARCHAR(16),      #手机
  fixedphone VARCHAR(16),     #固定电话
  postcode CHAR(6),           #邮编
  tag VARCHAR(16),            #标签名
  is_default BOOLEAN          #是否为当前用户的默认收货地址
);

/**购物车条目**/
CREATE TABLE flower_shoppingcart_item(
  id INT PRIMARY KEY AUTO_INCREMENT,
  lid INT,                  #商品编号
  uid INT,                  #用户编号
  img_url VARCHAR(64),		
  title VARCHAR(64),	    #商品详情
  price DECIMAL(10,2),
  count INT,                #购买数量
  is_checked BOOL DEFAULT 0  #是否已勾选，确定购买
);

/**用户订单**/
CREATE TABLE flower_order(
  aid INT PRIMARY KEY AUTO_INCREMENT,
  uid INT,
  address_id INT,
  status INT,             #订单状态  1-等待付款  2-等待发货  3-运输中  4-已签收  5-已取消
  order_time BIGINT,      #下单时间
  pay_time BIGINT,        #付款时间
  deliver_time BIGINT,    #发货时间
  received_time BIGINT    #签收时间
)AUTO_INCREMENT=10000000;

/**用户订单**/
CREATE TABLE flower_order_detail(
  did INT PRIMARY KEY AUTO_INCREMENT,
  order_id INT,           #订单编号
  product_id INT,         #产品编号
  count INT               #购买数量
);

